USER_NAME = "manabu.seki"

user USER_NAME do
  action :create
  home "/home/#{USER_NAME}"
end

directory "/home/#{USER_NAME}/.ssh" do
  group USER_NAME
  owner USER_NAME
  mode "0755"
  action :create
end

file "/home/#{USER_NAME}/.ssh/authorized_keys" do
  group USER_NAME
  owner USER_NAME
  mode "0644"
  content <<-EOC
  ssh-rsa  github@zitaku.net
  ssh-rsa 
  EOC
end
