USER_NAME = "shogo.ichinose"

user USER_NAME do
  action :create
  home "/home/#{USER_NAME}"
end

directory "/home/#{USER_NAME}/.ssh" do
  group USER_NAME
  owner USER_NAME
  mode "0755"
  action :create
end

file "/home/#{USER_NAME}/.ssh/authorized_keys" do
  group USER_NAME
  owner USER_NAME
  mode "0644"
  content <<'EOS'
ssh-rsa 
ssh-rsa  shogo.ichinose@fuller.co.jp
EOS
end
